import { EntityManager, System } from '../ecs_engine';
import { UpdateContext } from '../update_context';
import { Position } from '../components/position';

export class RenderBeacon implements System<UpdateContext> {
  name = 'RenderBeacon';

  onUpdate(em: EntityManager<UpdateContext>, context: UpdateContext): void {
    const entities = em.select(['Beacon', 'Position']);

    const ctx: CanvasRenderingContext2D = context.canvas2D;

    for (let [entity, componentsMap] of entities.entries()) {
      const pos = componentsMap.get('Position') as Position;
      this.render(pos, ctx);
    }
  }

  private render(pos: Position, ctx: CanvasRenderingContext2D) {
    const x = pos.position[0];
    const y = pos.position[1];
    const w = 40;
    const h = 40;
    const transX = w / 2;
    const transY = h / 2;

    ctx.save(); // save current state

    ctx.translate(x - transX, y - transY); // move to desired point

    // go to center for rotation
    ctx.translate(transX, transY);

    const sprite = new Image();
    sprite.src = this.base64BeaconSprite;
    ctx.drawImage(sprite, -transX, -transY, w, h); // draws the sprite

    ctx.restore(); // restore original states (no rotation etc)
  }

  private base64BeaconSprite = `data:image/png;base64,`;
}
